import React, { Component, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  Button,
  Image,
  ImageBackground,
  TouchableOpacity,
  Modal,
  TouchableHighlight,
  StatusBar,
  SafeAreaView,
  ScrollView,
  Linking,
} from "react-native";
import { globalStyles } from "../styles/global";
import Location from "../screens/locations";
import FitImage from "react-native-fit-image";
import { MaterialIcons } from "@expo/vector-icons";
//import { ScrollView } from 'react-native-gesture-handler';
import Eventsmodal from "../components/eventsmodal";
import { WebView } from 'react-native-webview';

const INJECTEDJAVASCRIPT = 'function hideHeaderToggle() {var headerToggle = document.getElementsByClassName("cco-header"), i;for (i = 0; i < headerToggle.length; i += 1) {headerToggle[i].style.display = "none";};}; hideHeaderToggle(); const meta = document.createElement(\'meta\'); meta.setAttribute(\'content\', \'width=device-width, initial-scale=1, maximum-scale=1, user-scalable=0\'); meta.setAttribute(\'name\', \'viewport\'); document.getElementsByTagName(\'head\')[0].appendChild(meta);'

export default class App extends Component {
  render() {
    let WebViewRef;
    return (
      <View >
        <WebView
          ref={WEBVIEW_REF => (WebViewRef = WEBVIEW_REF)}
          source={{ uri: this.state.site }}
          renderLoading={this.ActivityIndicatorLoadingView}
          startInLoadingState={true}
        />
        <Button title="Reload Me!" onpress={() => { WebViewRef && WebViewRef.reload(); }} />
      </View>
    )
  }
}
