import React, { Component, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  Button,
  Image,
  ImageBackground,
  TouchableOpacity,
  Modal,
  TouchableHighlight,
  StatusBar,
  SafeAreaView,
  ScrollView,
  Linking,
} from "react-native";
import { globalStyles } from "../styles/global";
import Location from "../screens/locations";
import FitImage from "react-native-fit-image";
import { MaterialIcons } from "@expo/vector-icons";
//import { ScrollView } from 'react-native-gesture-handler';
import Eventsmodal from "../components/eventsmodal";
import { WebView } from 'react-native-webview';

const INJECTEDJAVASCRIPT = 'const meta = document.createElement(\'meta\'); meta.setAttribute(\'content\', \'width=device-width, initial-scale=1, maximum-scale=1, user-scalable=0\'); meta.setAttribute(\'name\', \'viewport\'); document.getElementsByTagName(\'head\')[0].appendChild(meta); '
export default class MyWeb extends Component {
  mainUrl = "https://facebook.github.io/";
  state = {
    url: this.mainUrl,
  };

  render() {
    return [
      <WebView
        key="comp1"
        ref={
          ref => {
            this.webView.ref = ref;
          }
        }
        source={
          {
            uri: this.state.url
          }
        }
        onLoad={
          e => {
            // Update the state so url changes could be detected by React and we could load the mainUrl.
            this.state.url = e.nativeEvent.url;
          }
        }
    />,
    <Button
      key="comp2"
      onPress={
        () => {
          if (this.state.url == this.mainUrl) {
            this.webView.ref.reload();
          } else {
            this.setState(prevState => ({
              url: this.mainUrl
            }));
          }
        }
      }
    />
    ];
  }
}
