import React, { Component, useState,useCallback } from 'react';
import { StyleSheet, 
         View, 
         Text, 
         Button, 
         Image, 
         ImageBackground, 
         TouchableOpacity,
         Modal,
         TouchableHighlight, 
         StatusBar,
         SafeAreaView, 
         ScrollView, 
        Linking,
        }
          from 'react-native';
import { globalStyles } from '../styles/global';
import Location from '../screens/locations'
import FitImage from 'react-native-fit-image';
import { MaterialIcons } from '@expo/vector-icons';
import * as WebBrowser from 'expo-web-browser';
import Constants from 'expo-constants';
//import { ScrollView } from 'react-native-gesture-handler';

console.disableYellowBox = true;
export default function Home( {navigation} ) {

  const [result, setResult] = useState(null);

  const _handlePressButtonAsync = async () => {
    let result = await WebBrowser.openBrowserAsync('https://equippersdunedin.churchcenter.com/people/forms/118369');
    setResult(result);
  };


  const _handlePressButtonAsync2 = async () => {
    let result = await WebBrowser.openBrowserAsync('https://equipperschurch.com/athome');
    setResult(result);
  };

const [modalVisible, setModalVisible] = useState(false);
const image = require('../assets/connect04.jpeg')
const image2 = require('../assets/connect03.jpeg')
const image3 = require('../assets/connect02.jpeg')
const image4 = require('../assets/connect01.jpeg')


  return (

   <View style={globalStyles.connectContainer}>
   <SafeAreaView style={globalStyles.darkHeader}><Text style={globalStyles.headerText}>Equippers</Text></SafeAreaView>
   <ScrollView>
   <View style={globalStyles.connectContent}>
  <TouchableOpacity onPress={_handlePressButtonAsync} >
   <ImageBackground source={image} style={globalStyles.connectItem}><View style={globalStyles.overlay}/><Text style={globalStyles.connectText}>Prayer Request</Text></ImageBackground>
   </TouchableOpacity>

   <TouchableOpacity onPress={_handlePressButtonAsync2} >
   <ImageBackground source={image2} style={globalStyles.connectItem}><View style={globalStyles.overlay}/><Text style={globalStyles.connectText}>Church At Home</Text></ImageBackground>
   </TouchableOpacity>




       </View>
       </ScrollView>
   </View>


  );

}
